00192 
00193         while (iss >> num) {
00194             laik.addPaz(num);
00195         }
00196 
00197         vector<int> pazymiai = laik.getPaz();
00198         if (!pazymiai.empty()) {
00199             laik.setEgz(pazymiai.back());
